.menu-toggle {
  width: 73px;
  height: 73px;
  display: none;
  align-items: center;
  justify-content: center;

  padding: 0;
  margin-right: 30px;
  // box-sizing: border-box;
  background-color: $accent-color;
  border-radius: 50%;
  cursor: pointer;
  border: none;

  fill: $primary-text-color;
  &:hover,
  &:focus {
    fill: blue;
  }
  @media screen and (max-width: 767px) {
    display: flex;
  }
}

.menu-toggle__image {
  box-sizing: border-box;
  width: 45px;
  height: 45px;
  fill: aliceblue;
}
.menu-container {
  width: 100%;
  z-index: 5;

  @media screen and (min-width: 768px) {
    display: flex;
    align-items: center;
  }
  @media screen and (max-width: 767px) {
    position: fixed;
    top: 0;
    left: 0;
    width: 100vw;
    height: 100vh;
    padding: 40px 48px;
    background-color: $hero-text-color;

    transform: translateX(100%);
    transition: transform 250ms ease;
    // z-index: 999;
    &.is-open {
      transform: translateX(0);
    }
  }
}
.menu {
  // @media screen and(max-width:767px) {
  //   display: none;
  // }
  @media screen and (min-width: 768px) {
    display: flex;
    align-items: center;
    justify-content: center;
    margin-left: 88px;
  }
  @media screen and (min-width: 1960px) {
    margin-left: 93px;
  }
}
.menu__close {
  display: none;
  position: absolute;
  top: 10px;
  right: 15px;

  width: 40px;
  height: 40px;

  border: 1px solid transparent;
  border-radius: 15px;
  justify-content: center;
  align-items: center;
  background-color: inherit;
  cursor: pointer;
  transition: fill 250ms cubic-bezier(0.4, 0, 0.2, 1);
  &:hover,
  &:focus {
    fill: $accent-color;
  }
  @media screen and (max-width: 767px) {
    display: flex;
  }
}
.menu__icon {
  fill: inherit;
}
.menu__nav-list {
  @extend %list;
  @media screen and (min-width: 768px) {
    display: flex;
    margin: 0;
  }
}
.menu__nav-item {
  margin-bottom: 20px;

  &:last-child {
    margin-bottom: 0;
  }
  @media screen and (min-width: 480px) {
    margin-bottom: 32px;
  }
  @media screen and (min-width: 768px) {
    margin-right: 50px;
    margin-bottom: 0;

    &:last-child {
      margin-right: 0px;
    }
  }
}
.menu__nav-link {
  text-decoration: none;
  padding: 0;
  font-size: 30px;
  display: inline-block;
  font-weight: 500;

  line-height: 1.18;
  text-align: left;
  letter-spacing: 0.02em;
  color: $primary-text-color;
  position: relative;
  transition: color 250ms cubic-bezier(0.4, 0, 0.2, 1);
  // &.menu__nav-link--current {
  //   color: $accent-color;
  //   &::after {
  //     content: "";
  //     bottom: 0;
  //     left: 0;

  //     position: absolute;
  //     width: 100%;
  //     height: 4px;
  //     border-radius: 4px;
  //     background-color: $accent-color;
  //   }
  // }
  &:hover,
  &:focus {
    color: $accent-color;
  }
  @media screen and (min-width: 480px) {
    font-size: 40px;
  }
  @media screen and (min-width: 768px) {
    padding-bottom: 32px;
    padding-top: 32px;
    display: flex;

    font-size: 14px;
    line-height: 1.14;

    transition: color 250ms cubic-bezier(0.4, 0, 0.2, 1);
  }
}

sources.sass\\base\\_common.scss:
html {
    box-sizing: border-box;

  }
  *,
  *::before,
  *::after {
    box-sizing: inherit;
  }
  
  body {
    color: $primary-text-color;
    font-family: $primary-font;
    // letter-spacing: 0.0em;
  }
  h1,
  h2,
  h3,
  h4,
  h5,
  h6,
  p {
    margin: 0;
    padding: 0;
  }
  img {
    display: block;
    max-width: 100%;
    height: auto;
  }

  .visually-hidden {
    position: absolute;
    width: 1px;
    height: 1px;
    margin: -1px;
    padding: 0;
    overflow: hidden;
    border: 0;
    clip: rect(0 0 0 0);
  }
  // link {
  //   text-decoration: none;
  // }
  a { text-decoration: none; }

