}
.header__contacts {
  @extend %list;
  display: flex;
  margin-left: auto;
}
.header__contacts__link {
  padding: 0;
  display: flex;
  justify-content: end;
  transition: color 250ms cubic-bezier(0.4, 0, 0.2, 1);
}
.header__contacts__item {
  position: relative;
  margin-right: 31px;
  fill: $logo-color;
  
  &:last-child {
    margin: 0;
  }
  &.header__contacts__item--shop {
    fill: $accent-color;
    
    // stroke: $accent-color;

    &::before {
      content: '12';
      color: $accent-color;
      font-size: 25px;
      position: absolute;
      bottom: 45px;
      right: 9px;
    }
  }
}
// .header__contacts__link {

// }
.header__contacts__tel {
  fill: none;
  stroke: $logo-color;;
  transform: rotate(13deg);
  width: 50px;
}
.header__contacts__shop {
  fill: $accent-color;
}

