.main-nav {
  color: $primary-text-color;
  text-decoration: none;
  font-weight: 500;
  font-size: 14px;
  line-height: 1.14;
  letter-spacing: 0.02em;

  display: flex;
  align-items: center;
  

}
.main-nav__list {
  text-decoration: none;
  @extend %list;
  display: flex;
   margin-top: 0;
  margin-bottom: 0;
  @media screen and (max-width:766px) {
    flex-direction: column;
} 
  
 
}
.main-nav__item {
  margin-right: 5px;
 
  &:last-child {
    margin-right: 0px;
  }
}

.main-nav__link {
  text-decoration: none;
  // list-style: none;
  margin: 0;

  padding-bottom: 32px;
  padding-top: 32px;
  display: inline-block;
  position: relative;

  color: $primary-text-color;
  transition: color 250ms cubic-bezier(0.4, 0, 0.2, 1);
  &:hover,
  &:focus {
    color: $accent-color;
  }
  &.main-nav__link--current {
    color: $accent-color;
    &::after {
      content: "";
      bottom: 0;
      left: 0;
  
      position: absolute;
      width: 100%;
      height: 4px;
      border-radius: 4px;
      background-color: $accent-color;
    }
  }
  @media screen and (max-width:766px) {
    padding: 10px;
} 
}

