.menu-toggle {
  width: 73px;
  height: 73px;
  display: none;
  align-items: center;
  justify-content: center;

  padding: 0;
  margin-right: 30px;
  // box-sizing: border-box;
  background-color: $accent-color;
  border-radius: 50%;
  cursor: pointer;
  border: none;

  fill: $primary-text-color;
  &:hover,
  &:focus {
    fill: blue;
  }
  @media screen and (max-width: 767px) {
    display: flex;
  }
}

.menu-toggle__image {
  box-sizing: border-box;
  width: 45px;
  height: 45px;
  fill: $primary-br-color;
}
.menu-container {
  // width: 100%; было при мобильной верстве
  z-index: 5;

  @media screen and (min-width: 768px) {
    display: flex;
    align-items: center;
    background-color: $logo-color;
    
  }
  @media screen and (max-width: 767px) {
    position: fixed;
    top: 0;
    left: 0;
    width: 100vw;
    height: 100vh;
    padding: 40px 48px;
    background-color: $hero-text-color;

    transform: translateX(100%);
    transition: transform 250ms ease;
    // z-index: 999;
    &.is-open {
      transform: translateX(0);
    }
  }
}
.menu {
  // @media screen and(max-width:767px) {
  //   display: none;
  // }
  @media screen and (min-width: 768px) {
    display: flex;
    align-items: center;
    // justify-content: space-between;
    // max-width: 1920px;
    // padding-left: 188px;
    // padding-right: 188px;
  }
 
}
.menu__close {
  display: none;
  position: absolute;
  top: 10px;
  right: 15px;

  width: 40px;
  height: 40px;

  border: 1px solid transparent;
  border-radius: 15px;
  justify-content: center;
  align-items: center;
  background-color: inherit;
  cursor: pointer;
  transition: fill 250ms cubic-bezier(0.4, 0, 0.2, 1);
  &:hover,
  &:focus {
    fill: $accent-color;
  }
  @media screen and (max-width: 767px) {
    display: flex;
  }
}
.menu__icon {
  fill: inherit;
  
}
.menu__nav-list {
  @extend %list;
  @media screen and (min-width: 768px) {
    display: flex;
    margin: 0;
  }
}
.menu__nav-item {
  margin-bottom: 20px;
  // font-size: 30px;

  &:last-child {
    margin-bottom: 0;
  }
  
  @media screen and (min-width: 768px) {
    margin-right: 50px;
    margin-bottom: 0;

    &:last-child {
      margin-right: 0px;
    }
  }
}
.menu__nav-link {
  text-decoration: none;
  padding: 0;
  font-size: 30px;
  display: inline-block;
  font-weight: 500;

  line-height: 1.18;
  text-align: left;
  letter-spacing: 0.02em;
  color: $primary-text-color;
  position: relative;
  transition: color 250ms cubic-bezier(0.4, 0, 0.2, 1);
  
  &:hover,
